mapper.select:
select * from employee


        name like concat('%', #{name},'%')


limit ${(page-1)*pageSize},#{pageSize}

mapper.update:
update employee
name=#{name}, password=#{password}, phone=#{phone}, sex=#{sex}, id_number=#{idNumber}, status=#{status}, update_time=#{updateTime}, update_user=#{updateUser},
where id=#{id}
